```java
class Solution {
    public int divisorSubstrings(int num, int k) {
        String value = String.valueOf(num);

        int left = 0; //左侧索引

        int right = k-1; //右侧索引

        int temp = 0; // 被整除的值

        int result = 0; // 美丽值结果

        while(right < value.length()){
            for(int i = left;i< right+1; i++){
                int t = value.charAt(i) - '0';
                temp += t * Math.pow(10,right-i);
            }
            if(temp != 0 && num % temp == 0){
                result++;
            }
            //被整除的值归0
            temp = 0;
            //指针同时移动
            right++;
            left++;
        }
        return result;
    }


}
