class Solution {
    public int lengthOfLongestSubstring(String s) {
        //滑动窗口,左指针
        int left = 0;
        Set<Character> set = new HashSet<Character>();

        //最大长度
        int max = 0;

        for(int i = 0 ;i < s.length(); i++){

            //如果发现重复，则左指针加一
            while(set.contains(s.charAt(i))){
                set.remove(s.charAt(left++));
            }

            set.add(s.charAt(i));
            max = Math.max(max,i - left + 1);
        }

        return max;
    }

}
